import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer 
} from 'recharts';
import React, { useState, useEffect } from 'react';

function HistogramChart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://127.0.0.1:5000/hsr_character_stats/1005');
        const rawData = await response.json();
         // Round the ATK values
         const roundedData = rawData.map(item => Math.round(item.ATK));
         // Count frequency of each ATK value
         const frequencyMap = {};
         roundedData.forEach(value => {
           frequencyMap[value] = (frequencyMap[value] || 0) + 1;
         });
         // Convert frequency map to array of objects for Recharts
         const chartData = Object.keys(frequencyMap).map(key => ({
           ATK: parseInt(key), // Convert key back to number
           frequency: frequencyMap[key],
         }));
         setData(chartData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Histogram of Kafka Attack Value</h1>
      <ResponsiveContainer width="100%" height={400}>
        <BarChart
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="ATK" 
            type="category" // Set type to category for discrete x-axis values
            domain={['auto', 'auto']} // Set domain to auto to include all data points
          />
          <YAxis />
          <Tooltip 
            formatter={(value) => [`${value} times`, 'Frequency']}
          />
          <Bar dataKey="frequency" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default HistogramChart;
