import React, { useEffect, useState, useRef } from 'react';
import * as d3 from 'd3';

function StdDevChart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://127.0.0.1:5000/hsr_character_stats/1005');
        const data = await response.json();
        setData(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }
    fetchData();
  }, []);

  const svgRef = useRef();

  useEffect(() => {
    const svg = d3.select(svgRef.current);
    const margin = { top: 20, right: 20, bottom: 30, left: 40 };
    const width = +svg.attr('width') - margin.left - margin.right;
    const height = +svg.attr('height') - margin.top - margin.bottom;
    const g = svg.append('g').attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

    // Extract ATK values from data
    const atkValues = data.map(d => d.ATK);

    const x = d3.scaleLinear().range([0, width]);
    const y = d3.scaleLinear().range([height, 0]);

    // Scale the data to fit the chart
    x.domain(d3.extent(atkValues));
    y.domain([0, d3.max(atkValues)]);

    // Draw the line
    g.append('path')
      .datum(atkValues)
      .attr('fill', 'none')
      .attr('stroke', 'steelblue')
      .attr('stroke-width', 1.5)
      .attr('d', d3.line()
        .x((d, i) => x(i))
        .y(d => y(d.ATK))
      );

    // Add X Axis
    g.append('g')
      .attr('transform', 'translate(0,' + height + ')')
      .call(d3.axisBottom(x));

    // Add Y Axis
    g.append('g').call(d3.axisLeft(y));
  }, [data]);

  return <svg ref={svgRef} width={600} height={400}></svg>;
}

export default StdDevChart;
