import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer 
} from 'recharts';
import React, { useState, useEffect } from 'react';

function HistogramChart() {
  const [data, setData] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await fetch('http://127.0.0.1:5000/hsr_character_stats/1005');
        const data = await response.json();
        const sortedRoundedData = data.map(item => ({
          ...item,
          ATK: Math.round(item.ATK)
        })).sort((a, b) => a.ATK - b.ATK);
        setData(sortedRoundedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Histogram of Kafka Attack Value</h1>
      <ResponsiveContainer width="100%" height={400}>
        <BarChart
          data={data}
          margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis 
            dataKey="ATK" 
            type="category" // Set type to category for discrete x-axis values
            domain={['auto', 'auto']} // Set domain to auto to include all data points
          />
          <YAxis />
          <Tooltip />
          <Bar dataKey="ATK" fill="#8884d8" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default HistogramChart;
