import './output.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom"
import Header from './Components/Header.js'
import Footer from './Components/Footer.js'
import Home from "./Views/Home.js"
import About from "./Views/About.js"
import Average from "./Views/Average.js"

function App() {

  const bgImageUrl = {
    home: '../assets/pics/hsrHomeBg2.png',
    about: '../assets/pics/hsrWallpaper.jpg'
  };

  return (
    <div className="text-gray-100 h-screen"> 
      <Router>

        <Header />

        <div className="p-3 bg-cover bg-center">
          <Routes>
            <Route exact path="/" 
              element={<Home background={bgImageUrl.home} />}/>
            <Route path="/average" 
              element={<Average background={bgImageUrl.home} />}/>
            <Route path="/about" 
              element={<About background={bgImageUrl.about} />}/>
          </Routes>
        </div>

        <Footer />

      </Router>  
    </div>
  );
}

export default App;
